use data_encoding_macro::base32;
use digest::dev::{feed_rand_16mib, fixed_reset_test};
use digest::new_test;
use hex_literal::hex;
use tiger::{Digest, Tiger, Tiger2, TigerTree};

new_test!(tiger, "tiger", tiger::Tiger, fixed_reset_test);
new_test!(tiger2, "tiger2", tiger::Tiger2, fixed_reset_test);

#[test]
fn tiger_rand() {
    let mut h = Tiger::new();
    feed_rand_16mib(&mut h);
    assert_eq!(
        h.finalize()[..],
        hex!("d12f382ecf3250c14aca7726df15b999dfe99f905cf163d2")[..]
    );
}

#[test]
fn tiger2_rand() {
    let mut h = Tiger2::new();
    feed_rand_16mib(&mut h);
    assert_eq!(
        h.finalize()[..],
        hex!("1bb7a80144c97f831fdefb635477776dd6c164048ce5895d")[..]
    );
}

#[test]
fn tiger_empty() {
    let mut h = Tiger::new();
    h.update(b"");
    assert_eq!(
        h.finalize()[..],
        hex!("3293ac630c13f0245f92bbb1766e16167a4e58492dde73f3")[..]
    );
}

#[test]
fn tth_empty() {
    let mut h = TigerTree::new();
    h.update(b"");
    assert_eq!(
        h.finalize()[..],
        base32!("LWPNACQDBZRYXW3VHJVCJ64QBZNGHOHHHZWCLNQ=")[..],
    );
}

#[test]
fn tth_one_block() {
    let mut h = TigerTree::new();
    let content = hex!("deadbeef");
    h.update(content);
    assert_eq!(
        h.finalize()[..],
        base32!("6UT2WHO3YTGO3BLS4DHXVFUGATV4ZOKBJ22DRXA=")[..],
    );
}

#[test]
fn tth_blob() {
    let mut h = TigerTree::new();
    let data = include_bytes!(concat!("data/", "tiger.blb"));
    h.update(data);
    assert_eq!(
        h.finalize()[..],
        base32!("CJEUDKKLNJ7FOKZXZOJDFABDDM7Q5XDIL6ANHKI=")[..],
    );
}

#[test]
fn tth_two_blocks() {
    let content = hex!("");
    assert!((1025..2049).contains(&content.len()));
    let mut h = TigerTree::new();
    h.update(content);
    assert_eq!(
        h.finalize()[..],
        base32!("3NT5TZCSYOXWYMU7WZSHG66CG6FKGZFXJQ2GROY=")[..],
    );
}

#[test]
fn tth_three_blocks() {
    let content = hex!("");
    assert!((2049..3073).contains(&content.len()));
    let mut h = TigerTree::new();
    h.update(content);
    assert_eq!(
        h.finalize()[..],
        base32!("6OTDZEQBTT73OUT6TLP2K6Q6ZN4DOKHWPMDP5UA=")[..],
    );
}
